import React, { Component } from 'react'

export default class Search extends Component {
    constructor(props) {
        super(props);
        //state in react
        this.state = {

            finalData: [],
            result: []
        }
    }
    async componentDidMount() {
        let url = `https://json.extendsclass.com/bin/8a056bf555b5`;

        let data = await fetch(url);
        let parsedData = await data.json()
        console.log("data", parsedData);
        this.setState({
            finalData: parsedData
        })
    }
    check(obj, str) {
        return obj.city === str
    }
    askserch(str) {
        this.setState({
            result: this.state.finalData.filter(check(str))
        })
    }


    render() {
        return (

            <div className='row'>


                {this.state.finalData.map((element, index) => {
                    return <div key={index} className=" col-sm-12 col-md-6 col-lg-4">

                        <div className="card" style={{ width: "18rem" }}>
                            {/* <img src="..." className="card-img-top" alt="..."> */}
                            <div className="card-body">
                                <h5 className="card-title">    City: {element.city}</h5>
                                <p className="card-text">
                                    population:{element["population 2"]}<br />
                                    state: {element.admin}
                                    {/* population: {` ${element.} `} <br />
                                    per_sq_km:{element.}<br />
                                    growth_rate:{element.} */}
                                </p>
                                {/* <a href="#" className="btn btn-primary">Go somewhere</a> */}
                            </div>
                        </div>

                        <br />
                    </div>
                })}


            </div>

            // <div>Search</div>
        )
    }
}
